import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, ArrowRight, ArrowLeft } from 'lucide-react';

const LoveLetter = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showContent, setShowContent] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    setIsVisible(true);
    setTimeout(() => setShowContent(true), 1000);
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-rose-50 p-4 relative overflow-hidden">
      {/* Floating Hearts Animations */}
      {Array(25).fill(null).map((_, i) => (
        <Heart
          key={`float-${i}`}
          className="absolute -top-12 animate-float"
          style={{
            left: `${Math.random() * 100}%`,
            color: '#fecdd3',
            width: `${12 + Math.random() * 20}px`,
            height: `${12 + Math.random() * 20}px`,
            opacity: 0.6,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${5 + Math.random() * 5}s`
          }}
        />
      ))}

      <div className={`
        bg-white rounded-lg shadow-lg p-4 md:p-8 
        max-w-md w-full mx-4 
        transform transition-all duration-800 ease-out
        ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-12 opacity-0'}
        relative z-10
      `}>
        {/* Corner Hearts */}
        {[0, 1, 2, 3].map((i) => (
          <Heart
            key={`corner-${i}`}
            className="absolute animate-cornerPulse"
            style={{
              color: '#f43f5e',
              width: '20px',
              height: '20px',
              opacity: 0.8,
              animationDelay: `${i * 0.5}s`,
              ...{
                0: { top: '1rem', left: '1rem' },
                1: { top: '1rem', right: '1rem' },
                2: { bottom: '1rem', left: '1rem' },
                3: { bottom: '1rem', right: '1rem' }
              }[i]
            }}
          />
        ))}

        <div className="text-center mb-6">
          <h1 className={`
            text-2xl md:text-3xl text-gray-800 mb-4
            ${showContent ? 'animate-fadeIn' : ''}
          `}>
            My Eyes
          </h1>
          <div className={`
            w-12 h-0.5 bg-rose-400 mx-auto
            ${showContent ? 'animate-widthExpand' : ''}
          `} />
        </div>

        <div className={`
          space-y-4 text-gray-600 
          transition-all duration-1000 ease-out delay-500
          ${showContent ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-5'}
        `}>
          <p className="text-base md:text-lg leading-relaxed">
            Every moment we share together fills my heart with joy and gratitude. Your smile brightens my darkest days, and your love gives me strength I never knew I had.
          </p>
          
          <p className="text-base md:text-lg leading-relaxed">
            You are the missing piece that makes my life complete, the melody to my song, and the answer to my prayers. I cherish every laugh we share, every quiet moment, and even the challenges we face together.
          </p>

          <p className="italic text-right mt-8">
            Forever Yours,<br />
            With all my love
          </p>

          <div className="flex flex-col sm:flex-row gap-4 mt-6">
            <button
              onClick={() => navigate('/Bigheart')}
              className="flex items-center justify-center 
                        w-full py-3 px-4
                        bg-rose-400 hover:bg-rose-500
                        text-white rounded-md
                        transition-colors duration-300
                        text-sm md:text-base"
            >
              Next Page
              <ArrowRight className="w-5 h-5 ml-2" />
            </button>
            
            <button
              onClick={() => navigate('/lovecard')}
              className="flex items-center justify-center 
                        w-full py-3 px-4
                        bg-rose-400 hover:bg-rose-500
                        text-white rounded-md
                        transition-colors duration-300
                        text-sm md:text-base"
            >
              Previous Page
              <ArrowLeft className="w-5 h-5 ml-2" />
            </button>
          </div>
        </div>
      </div>

      <style>{`
        @keyframes float {
          0% { transform: translateY(110vh) rotate(0deg); opacity: 0.6; }
          100% { transform: translateY(-10vh) rotate(360deg); opacity: 0; }
        }
        @keyframes cornerPulse {
          0% { transform: scale(1); }
          50% { transform: scale(1.2); }
          100% { transform: scale(1); }
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(-20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes widthExpand {
          from { width: 0; }
          to { width: 48px; }
        }
      `}</style>
    </div>
  );
};
